// Считает количество делителей

int Div(unsigned long long number)
{
	int count = 0;

	int maxdel = (int)sqrt(number);

	for (int i = 1; i <= sqrt(number); i++)
	{
		if (number % i == 0)
			count++;
	}

	if (maxdel == sqrt(number))
		count--;

	return count;
}

unsigned long long Ex12()
{
	unsigned long long number = 0;

	long j = 1;

	int count = 0;

	while (count < 250)
	{
		number = number + j;

		count = Div(number);

		j++;
	}

	return number;
}

