int Ex14()
{
	long int answer = 0;

	long int count = 0;

	for (long int n = 1000000; n > 1; n--)
	{
		long int amount = 1;

		unsigned long long int g = n;

		while (g > 1)
		{
			if (g % 2 == 0)
			{
				g /= 2;
			}
			else
			{
				g = 3 * g + 1;
			}

			amount++;
		}

		if (amount > count)
		{
			count = amount;
			answer = n;
		}
	}

	printf("%d\n", count);

	return answer;
}

