		for (int i = 0; prime_number[i] != 0 && flag == 0; i++)
		{
			if (number % prime_number[i] == 0)
				flag = 1;

			end_place++;
		}

		if (flag == 0)
		{
			sum += number;

			prime_number[end_place] = number;

			prime_number[end_place + 1] = 0;
		}
